import React, { useContext, useState } from 'react'
import { GlobalStoreContext } from '../store'

function SongCard(props) {
    const { store } = useContext(GlobalStoreContext);

    const { song, index } = props;
    let cardClass = "list-card unselected-list-card";

    const [ isDragging, setDragging ] = useState(false);
    const [ draggedTo, setDraggedTo ] = useState(false);

    function handleDeleteSong(event){
        event.stopPropagation();
        //let index = event.target.id.substring("remove-song-".length);
        store.deleteSong(index);
    }

    /*function handleClick (event) {
        event.stopPropagation();
        this.props.editSongCallback(this.props.index, this.props.song);
    }*/

    function handleDragStart (event) {
        event.dataTransfer.setData("song", event.target.id);
        setDragging(true);
        setDraggedTo(false);
    }
    function handleDragOver (event) {
        event.preventDefault();
        setDragging(false);
        setDraggedTo(true);
    }

    function handleDragEnter (event) {
        event.preventDefault();
        setDragging(false);
        setDraggedTo(true);
    }

    function handleDragLeave (event) {
        event.preventDefault();
        setDraggedTo(false)
    }

    function handleDrop (event) {
        event.preventDefault();
        /*let target = event.target;
        let targetId = target.id;
        targetId = (targetId.substring(target.id.indexOf("-")+1)).split('-')[0];
        let sourceId = event.dataTransfer.getData("song");
        sourceId = (sourceId.substring(sourceId.indexOf("-")+1)).split('-')[0];*/

        setDragging(false);
        setDraggedTo(false);

        // ASK THE MODEL TO MOVE THE DATA
        //store.moveCallback(sourceId, targetId);
    }


    return (
        <div
            key={index}
            id={'song-' + index + '-card'}
            className={cardClass}
            //onDoubleClick={handleClick}
            onDragStart={handleDragStart}
            onDragOver={handleDragOver}
            onDragEnter={handleDragEnter}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            draggable="true"
        >
            {index + 1}.
            <a
                id={'song-' + index + '-link'}
                className="song-link"
                href={"https://www.youtube.com/watch?v=" + song.youTubeId}>
                {song.title} by {song.artist}
            </a>
            <input
                type="button"
                id={"remove-song-" + index}
                className="list-card-button"
                value={"\u2715"}
                onClick={handleDeleteSong}
            />
        </div>
    );
}

export default SongCard;
