import React, { useContext, useState } from 'react'
import { GlobalStoreContext } from '../store'

function SongCard(props) {
    const { store } = useContext(GlobalStoreContext);

    const { song, index } = props;
    let cardClass = "list-card unselected-list-card";

    function handleDeleteSong(event){
        event.stopPropagation();
        //let index = event.target.id.substring("remove-song-".length);
        store.deleteSong(index);
    }

    function handleClick (event) {
        event.stopPropagation();
        store.editSong(index,song);
    }

    function handleDragStart (event) {
        event.dataTransfer.setData("song", this.props.index);
    }

    function handleDragOver (event) {
        event.preventDefault();
    }

    function handleDragEnter (event) {
        event.preventDefault();
    }

    function handleDragLeave (event) {
        event.preventDefault();
    }

    function handleDrop (event) {
        event.preventDefault();
        let targetIndex = this.props.index;
        let sourceIndex = Number(event.dataTransfer.getData("song"));

        // ASK THE MODEL TO MOVE THE DATA
        store.move(sourceIndex, targetIndex);
    }


    return (
        <div
            key={index}
            id={'song-' + index + '-card'}
            className={cardClass}
            onDoubleClick={handleClick}
            onDragStart={handleDragStart}
            onDragOver={handleDragOver}
            onDragEnter={handleDragEnter}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            draggable="true"
        >
            {index + 1}.
            <a
                id={'song-' + index + '-link'}
                className="song-link"
                href={"https://www.youtube.com/watch?v=" + song.youTubeId}>
                {song.title} by {song.artist}
            </a>
            <input
                type="button"
                id={"remove-song-" + index}
                className="list-card-button"
                value={"\u2715"}
                onClick={handleDeleteSong}
            />
        </div>
    );
}

export default SongCard;
