import { useContext } from 'react'
import { GlobalStoreContext } from '../store'
import { useHistory } from 'react-router-dom'
/*
    This toolbar is a functional React component that
    manages the undo/redo/close buttons.

    @author McKilla Gorilla
*/
function EditToolbar() {
    const { store } = useContext(GlobalStoreContext);
    const history = useHistory();

    let enabledButtonClass = "playlister-button";
    let UndoClass = "playlister-button;"
    let redoClass = "playlister-button";

    /*if (store.markListDeleteId != 0) {
        enabledButtonClass = "playlister-button-disabled";
    }*/

    if (store.currentList == null) {
        enabledButtonClass = "playlister-button-disabled";
    }

    if (!store.canUndo) {
       UndoClass = "playlister-button-disabled";
    } else {
        UndoClass = "playlister-button";
    }

    if (!store.canRedo) {
        redoClass = "playlister-button-disabled";
     } else {
        redoClass = "playlister-button";
     }


    function handleUndo() {
        store.undo();
    }
    function handleRedo() {
        store.redo();
    }
    function handleClose() {
        history.push("/");
        store.closeCurrentList();
    }

    function handleAddsong() {
        store.addAddSongTransaction();
        //store.addSong();
    }
    let editStatus = false;
    if (store.listNameEditActive) {
        editStatus = true;
    }
    return (
        <span id="edit-toolbar">
            <input
                type="button"
                id='add-song-button'
                disabled={editStatus}
                value="+"
                className={enabledButtonClass}
                onClick={handleAddsong}
            />
            <input
                type="button"
                id='undo-button'
                disabled={editStatus}
                value="⟲"
                className={UndoClass}
                onClick={handleUndo}
            />
            <input
                type="button"
                id='redo-button'
                disabled={editStatus}
                value="⟳"
                className={redoClass}
                onClick={handleRedo}
            />
            <input
                type="button"
                id='close-button'
                disabled={editStatus}
                value="&#x2715;"
                className={enabledButtonClass}
                onClick={handleClose}
            />
        </span>);
}

export default EditToolbar;
