import { useContext } from 'react'
import { GlobalStoreContext } from '../store'
import { useHistory } from 'react-router-dom'
/*
    This toolbar is a functional React component that
    manages the undo/redo/close buttons.

    @author McKilla Gorilla
*/
function EditToolbar() {
    const { store } = useContext(GlobalStoreContext);
    const history = useHistory();

    let enabledAddButtonClass = "playlister-button";
    let enabledUndoButtonClass = "playlister-button";
    let enabledRedoButtonClass = "playlister-button";
    let enabledCloseButtonClass = "playlister-button";

    if (store.currentList == null ){
        enabledAddButtonClass += "playlister-button-disabled";
        enabledUndoButtonClass += "playlister-button-disabled";
        enabledRedoButtonClass += "playlister-button-disabled";
        enabledCloseButtonClass += "playlister-button-disabled";
    }

    if (!store.canUndo) enabledUndoButtonClass += "playlister-button-disabled";
    if (!store.canRedo) enabledRedoButtonClass += "playlister-button-disabled";


    function handleUndo() {
        store.undo();
    }
    function handleRedo() {
        store.redo();
    }
    function handleClose() {
        history.push("/");
        store.closeCurrentList();
    }

    function handleAddsong() {
        store.addAddSongTransaction();
        //store.addSong();
    }
    let editStatus = false;
    if (store.listNameEditActive) {
        editStatus = true;
    }
    return (
        <span id="edit-toolbar">
            <input
                type="button"
                id='add-song-button'
                disabled={editStatus}
                value="+"
                className={enabledAddButtonClass}
                onClick={handleAddsong}
            />
            <input
                type="button"
                id='undo-button'
                disabled={!store.canUndo}
                value="⟲"
                className={enabledUndoButtonClass}
                onClick={handleUndo}
            />
            <input
                type="button"
                id='redo-button'
                disabled={editStatus}
                value="⟳"
                className={enabledRedoButtonClass}
                onClick={handleRedo}
            />
            <input
                type="button"
                id='close-button'
                disabled={editStatus}
                value="&#x2715;"
                className={enabledCloseButtonClass}
                onClick={handleClose}
            />
        </span>);
}

export default EditToolbar;
